import React from 'react'
import './NewBeer.css'

class NewBeer extends React.Component {
    state = {
        name:"",
        tagline:"",
        description:"",
        first_brewed:"",
        brewer_tips:"",
        attenuation_level:"",
        contributed_by:""
    }

    handleCreate = e => {
      e.preventDefault();

      axios.post(`https://ih-beers-api2.herokuapp.com/beers/new`, this.state)
        .then(res => {
          console.log(res);
          console.log(res.data);
        })

    }

    handleChange = e => {
      let change = {};
      change[e.target.name] = e.target.value;
      console.log(change)
      this.setState(change)
    }

    render(){

            return (
                <div className="post">
                  <h2 className="title-form">Create a new beer!</h2> 
                  <form className="post" onSubmit={this.handleCreate}>
                    <div className="form-group">
                      <input
                          placeholder="Name" name='name' className="form-control" onChange={this.handleChange} value={this.state.name}
                            />
                    </div>

                    <div className="form-group">
                      <input
                        placeholder="TagLine"  name='tagline' className="form-control" onChange={this.handleChange} value={this.state.tagline}
                      />
                    </div>

                    <div className="form-group">
                      <input
                        placeholder="First brewed"  name="first_brewed" className="form-control" onChange={this.handleChange} value={this.state.firstBrewed}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        placeholder="Brewed tips" name="brewed_tips" className="form-control"  onChange={this.handleChange} value={this.state.brewedFirst}
                      />
                    </div>

                    <div className="form-group">
                      <input
                        placeholder="Attenuation" name="attenuation" className="form-control" onChange={this.handleChange} value={this.state.attenuation}
                      />
                    </div>

                    <div className="form-group">
                      <input
                        placeholder="Contributed By" name="contributed_by" className="form-control"  onChange={this.handleChange}  value={this.state.cotributedBy}
                      />
                    </div>
                    <div className="form-group">
                      <textarea
                        placeholder="Description" name='description' className="form-control" onChange={this.handleChange} value={this.state.description}
                      />
                    </div>

                    <button type="submit" className="btn btn-primary">Create Beer</button>
                  </form>
                </div>


            )
    }

}

export default NewBeer
