import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import'./BeerList.css'




class BeerList extends React.Component {
    state={ 
        beerList:[],
        filter: '',
        loading: true
    }
    componentDidMount(){
        axios.get("https://ih-beers-api2.herokuapp.com/beers")
        .then(response => {
            console.log(response.data)
            if(this.state.loading){
                this.setState({loading:false})


            } else {
                this.setState({
                    beerList: response.data
                })
            }

        })
    }
    handleChange = (e) => {
        let filterValue = e.target.value;
        this.setState({
            filter:e.target.value,
        })

        //EX: GET: "/search?name=Beer1&size=2l"
        axios.get("https://ih-beers-api2.herokuapp.com/beers/search?q="+filterValue)
        .then(res => {
            this.setState({
                beerList:res.data,
            })
        })
    }



    render() {
        return ( this.state.loading ? `<div>
        <input
                        placeholder="Filter by name"
                        name='tagline' className="form-control"
                        onChange={this.handleChange}
                        value={this.state.filter}
                    />
            {this.state.beerList.map(value  => {
                return (
                    <Link to={\`/beer/${value._id}\`} >


                    <div className="card">

                        <img src={value.image_url} className="card-img-top" alt="..."/>
                        <div className="card-body">
                            <h5 className="card-title">{value.name}</h5>
                            <p className="card-text">{value.description}</p>
                            <p className="card-text">{value.contributed_by}</p>
                        </div>
                    </div>

                    </Link>


                )
            })}

    </div>` : ` <div><button class="btn btn-primary" type="button" disabled>
    <span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
    <span class="sr-only">Loading...</span>
  </button>
  <button class="btn btn-primary" type="button" disabled>
    <span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
    Loading...
  </button> </div>`


        )
    }
}

export default BeerList;
